api/store.html.body:
storeCreateimport {storeCreate} from 'fluder';
import constants from '../constants'
import todoAction from '../actions/todoAction'

var items = [];
export default storeCreate(todoAction, {

  /**
   * store只提供读权限
   */
  getAll: function(){
    return items
  }
},{

  /**
   * handler提供读写权限
   */
  [constants.ADD_TODO]: function(payload){
    push(payload.value)
    return items
  }
})

/**
 * 写权限API
 */
function push(item){
  items.push(item)
}

api/actionStore.html.body:
actionStoreCreate//todoActionStore.js
import {actionStoreCreate} from 'fluder';
import constants from '../constants'
/**
 * Action和Store一并init
 */
var items = [];
export default actionStoreCreate({
    addTodo:(item)=>({
      type: constants.ADD_TODO,
      value: item
    })
}, {

  /**
   * store只提供读权限
   */
  getAll: function(){
    return items
  }
}, {

  /**
   * handler提供读写权限
   */
  [constants.ADD_TODO]: function(payload){
    push(payload.value)
    return items
  }
})//storeId选填[内部统一了ID]

/**
 * 写权限API
 */
function push(item){
  items.push(item)
}

