storeCreateimport {storeCreate} from 'fluder';
import constants from '../constants'
/**
 * 和Action里面的ID对应
 * @type {String}
 */
const TODOAPP_ID = "TODOAPP";

export default storeCreate(TODOAPP_ID,{

  /**
   * store只提供读权限
   */
  getAll: function(){
    return items
  }
},{

  /**
   * handler提供读写权限
   */
  [`${TODOAPP_ID}/${constants.ADD_TODO}`]: function(payload){
    push(payload.value)
    return items
  }
})

/**
 * 写权限API
 */
function push(item){
  items.push(item)
}

api/actionStore.html.body:
actionStoreCreateimport {actionStoreCreate} from 'fluder';
import constants from '../constants'
/**
 * Action和Store一并init
 */
export default actionStoreCreate({
    addTodo:(item)=>({
      type: `${TODOAPP_ID}/${constants.ADD_TODO}`,
      value: item
    })
}, {

  /**
   * store只提供读权限
   */
  getAll: function(){
    return items
  }
}, {

  /**
   * handler提供读写权限
   */
  [`${TODOAPP_ID}/${constants.ADD_TODO}`]: function(payload){
    push(payload.value)
    return items
  }
})//storeId选填[内部统一了ID]

/**
 * 写权限API
 */
function push(item){
  items.push(item)
}

